mapper.update:
      UPDATE applet_lock_repository a
        SET a.`status` = ${openStatus}
      WHERE
	    a.id IN (
	      SELECT b.id FROM
	        ( SELECT id FROM
	          applet_lock_repository
	            WHERE start_time = DATE_FORMAT( ${startTime}, '%Y-%m-%d' ) AND STATUS = ${noneStatus}
	        )b
	    )

      UPDATE applet_lock_repository a
        SET a.`status` = ${endStatus}
      WHERE
	    a.id IN (
	      SELECT b.id FROM
	        ( SELECT id FROM
	          applet_lock_repository
	            WHERE end_time = DATE_FORMAT( ${endTime}, '%Y-%m-%d' ) AND STATUS = ${openStatus}
	        )b
	    )
